css:
.container {
    font-family: Arial, sans-serif;
    width: 100%;
    padding: 20px;
    box-sizing: border-box;
}

.header {
    text-align: center;
    margin-bottom: 20px;
}

.header h1 {
    margin: 0;
    font-size: 35px;
    color: #333;
}

.content p {
    margin: 10px 0;
    font-size: 25px;
    color: #555;
}

.content strong {
    color: #333;
}

.content img {
    max-width: 100%;
    height: auto;
    margin-top: 10px;
    border: 1px solid #ccc;
    padding: 5px;
}

.content select {
    padding: 5px;
    font-size: 14px;
}
