css:
body {
    font-family: 'Arial', sans-serif; /* fallback fonts */
    line-height: 1.6;
    margin: 0; /* Remove default margin */
    padding: 20px;
    background-color: #f0f0f0; /* Light background for the whole page */
}
.container {
    width: 80%;
    max-width: 600px;
    margin: 0 auto;
    padding: 20px;
    border: 1px solid #ccc;
    border-radius: 10px; /* Rounded corners for the container */
    background-color: #fff; /* Ensure container has a background */
    box-shadow: 0 0 10px rgba(0,0,0,0.1); /* Add a subtle shadow */
}
.header {
    background-color: #3498db; /* Soft blue for the header */
    padding: 20px;
    text-align: center;
    margin-bottom: 20px;
    border-radius: 10px 10px 0 0; /* Rounded corners only on top */
    color: #fff; /* White text color for the header */
}
.content {
    margin-top: 20px;
    margin-bottom: 20px;
    padding: 10px; /* Add padding for content */
}
.footer {
    text-align: center;
    font-style: italic;
    color: #888; /* Adjust footer text color for better readability */
    padding: 10px; /* Add padding for footer */
    border-radius: 0 0 10px 10px; /* Rounded corners only on bottom */
    background-color: #2c3e50; /* Darker blue for the footer */
    color: white; /* White text color for the footer */
}
.articles {
    margin-top: 30px;
}
.articles table {
    width: 100%;
    border-collapse: collapse;
}
.articles th, .articles td {
    padding: 15px;
    text-align: left;
}
.articles th {
    background-color: #27ae60; /* Green background for table header */
    color: #fff; /* White text color for table header */
}
.articles td {
    border: 1px solid #ddd; /* Light border for table cells */
}
